import collections


# noinspection DuplicatedCode
class Solution:
    def checkInclusion(self, s1: str, s2: str) -> bool:
        size = len(s1)

        if len(s2) < size or len(s2) < 1:
            return False

        expected_counts = collections.Counter(s1)
        expected_chars = sum(expected_counts.values())

        chars = expected_chars
        counts = dict(expected_counts)

        for i, c in enumerate(s2):
            first_pos = i - size + chars

            if c not in counts:
                # we ran out of this char, so this fragment can't be an anagram
                if chars != expected_chars:
                    chars = expected_chars
                    counts = dict(expected_counts)
                continue

            if counts[c] == 0:
                # we ran out of this char... reset counters (partially)
                for j in range(first_pos, len(s2)):
                    this_c = s2[j]
                    if this_c == c:
                        break
                    chars += 1
                    counts[this_c] += 1
                continue

            counts[c] -= 1
            chars -= 1

            if chars == 0:
                return True

        return False


test_cases = (
    (True, "ab", "eidbaooo"),
    (False, "ab", "eidboaoo"),
    (True, "abc", "bbbca"),
    (False, "kitten", "sitting"),
    (True, "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
     ""),
)

# unit tests
for pair in test_cases:
    expected, large, small = pair

    result = Solution().checkInclusion(large, small)

    message = "OK" if result == expected else "ERROR"

    print(f'{message:6}: expected: {expected}, result: {result}, input: {large, small}')
